/**
 * The verysimpledownloadModerationObjects plugin determines the amount of unapproved objects.
 * It uses the same logic as the moderation block and the pending content listener.
 *
 * @param  array       $params All attributes passed to this function from the template.
 * @param  Zikula_View $view   Reference to the view object.
 */
function smarty_function_verysimpledownloadModerationObjects($params, $view)
{
    if (!isset($params['assign']) || empty($params['assign'])) {
        $view->trigger_error(__f('Error! in %1$s: the %2$s parameter must be specified.', array('verysimpledownloadModerationObjects', 'assign')));

        return false;
    }

    $serviceManager = $view->getServiceManager();
    $workflowHelper = new VerySimpleDownload_Util_Workflow($serviceManager);
    $result = $workflowHelper->collectAmountOfModerationItems();

    $view->assign($params['assign'], $result);
}
